import { useContext } from "react";
import { HodiContext } from "./Context";

const Comments = () => {

    const {comments}=useContext(HodiContext);

    return ( 
        <div className="flex">
       <div>
            <table>
                <tr>
                    <th>Name</th>
                    <th>Body</th>
                </tr>    
                {comments.map(comment=><tr><th>{comment.name}</th><th>{comment.body}</th></tr>)}

            </table>  
        </div>  
        <div>

        <div class="mb-3">

        <label for="name" className="form-label">Name</label>
        <input name='name' type="text" className="form-control" value='' placeholder='name' onChange={(e)=>setName(e.target.value)}></input>

        </div>

        <div class="mb-3">

        <label for="name" className="form-label">Name</label>
        <input name='name' type="text" className="form-control" value='' placeholder='name' onChange={(e)=>setName(e.target.value)}></input>

        </div>
        </div>
        </div>
     );
}
 
export default Comments;
