import { HodiContext } from './Context';
import { useContext } from 'react';

const NavBar = () => {

    const {action,books,actionType,setActionType,name,author,setName,setAuthor,bwc,setBwc,display,setDisplay,manage,setManage,btn,setBtn,formState,setFormState,cerruntPage}=useContext(HodiContext);

    return ( 

        <div className='row r1'>

                <div className='col-8'> 

                    <article> Bookily</article>
                    <button><a href="/logIn">Log In</a></button>
                    <button><a href="/signIn">Sign in</a></button>
                </div>    

                <div className='col-4'> 

                    <button className={btn} onClick={()=>{setBtn(btn==="btn"?"btn disabled":"btn");
                            setDisplay(display==="Display"?"DisplayDis":"Display");
                            setManage(manage==="Manage"?"ManagePop":"Manage");
                            setActionType('post');
                            return;}}>Log In</button>

                </div>   
            </div>   

     );
}
 
export default NavBar;
